// src/components/Auth/LoginForm.jsx
import React, { useState, useContext } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  IconButton,
  InputAdornment,
  Paper,
  Divider,
} from '@mui/material';
import {
  Visibility,
  VisibilityOff,
  Email as EmailIcon,
  Lock as LockIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import { AuthContext } from '../../context/AuthContext';
import { GoogleLogin } from '@react-oauth/google';

const LoginForm = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [correo, setCorreo] = useState('');
  const [contraseña, setContraseña] = useState('');
  const [mostrarContraseña, setMostrarContraseña] = useState(false);
  const [error, setError] = useState('');

  const toggleMostrarContraseña = () => setMostrarContraseña(prev => !prev);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/usuarios/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ correo, contraseña }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.message || 'Credenciales incorrectas.');
        return;
      }

      if (data.usuario?.token) {
        const token = data.usuario.token;
        login(token);
        const decoded = jwtDecode(token);

        if (decoded.rol === 'superadmin' || decoded.rol_id === 1)
          navigate('/admin/dashboard');
        else if (decoded.rol === 'cliente' || decoded.rol_id === 2)
          navigate('/cliente/dashboard');
        else
          setError('Rol no autorizado para ingresar.');
      } else {
        setError('No se recibió token en la respuesta.');
      }
    } catch (err) {
      console.error('[LoginForm] Error:', err);
      setError('Error de conexión al servidor.');
    }
  };

  const handleGoogleSuccess = async (credentialResponse) => {
    const { credential } = credentialResponse;
    if (!credential) {
      setError('No se recibió token de Google.');
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/api/usuarios/login-google', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tokenGoogle: credential }),
      });

      const data = await res.json();
      if (!res.ok) {
        setError(data.message || 'Error al iniciar sesión con Google.');
        return;
      }

      if (data.usuario?.token) {
        const token = data.usuario.token;
        login(token);
        const decoded = jwtDecode(token);

        if (decoded.rol === 'superadmin' || decoded.rol_id === 1)
          navigate('/admin/dashboard');
        else if (decoded.rol === 'cliente' || decoded.rol_id === 2)
          navigate('/cliente/dashboard');
        else
          setError('Rol no autorizado para ingresar.');
      } else {
        setError('No se recibió token del servidor.');
      }
    } catch (error) {
      console.error('[GoogleLogin] Error:', error);
      setError('Error al iniciar sesión con Google.');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 4, width: '100%', maxWidth: 400 }}>
      <Typography variant="h5" align="center" gutterBottom>
        Iniciar Sesión
      </Typography>
      <form onSubmit={handleSubmit}>
        <Box mb={2}>
          <TextField
            label="Correo electrónico"
            fullWidth
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
            required
            type="email"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <EmailIcon color="action" />
                </InputAdornment>
              ),
            }}
          />
        </Box>
        <Box mb={2}>
          <TextField
            label="Contraseña"
            fullWidth
            value={contraseña}
            onChange={(e) => setContraseña(e.target.value)}
            required
            type={mostrarContraseña ? 'text' : 'password'}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <LockIcon color="action" />
                </InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={toggleMostrarContraseña} edge="end">
                    {mostrarContraseña ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </Box>
        {error && (
          <Typography color="error" mb={2} textAlign="center">
            {error}
          </Typography>
        )}
        <Button type="submit" variant="contained" fullWidth size="large">
          Iniciar Sesión
        </Button>
      </form>

      <Divider sx={{ my: 2 }}>o</Divider>

      <Box display="flex" justifyContent="center">
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={() => setError('Fallo el inicio de sesión con Google.')}
        />
      </Box>
    </Paper>
  );
};

export default LoginForm;
