import React, { useState, useContext } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  IconButton,
  InputAdornment,
  Paper,
  Divider
} from '@mui/material';
import {
  Visibility,
  VisibilityOff,
  Email as EmailIcon,
  Lock as LockIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import { AuthContext } from '../../context/AuthContext';

// 游녤 Nuevo import
import { GoogleLogin } from '@react-oauth/google';

const LoginForm = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [correo, setCorreo] = useState('');
  const [contrase침a, setContrase침a] = useState('');
  const [mostrarContrase침a, setMostrarContrase침a] = useState(false);
  const [error, setError] = useState('');

  const toggleMostrarContrase침a = () => {
    setMostrarContrase침a((prev) => !prev);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/usuarios/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ correo, contrase침a }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || 'Credenciales incorrectas.');
        return;
      }

      if (data.usuario && data.usuario.token) {
        const token = data.usuario.token;
        login(token);

        const decoded = jwtDecode(token);

        if (decoded.rol === 'superadmin' || decoded.rol_id === 1) {
          navigate('/admin/dashboard');
        } else if (decoded.rol === 'cliente' || decoded.rol_id === 2) {
          navigate('/cliente/dashboard');
        } else {
          setError('Rol no autorizado para ingresar.');
        }
      } else {
        setError('No se recibi칩 token en la respuesta.');
      }
    } catch (err) {
      console.error('[LoginForm] Error en login:', err);
      setError('Error de conexi칩n al servidor.');
    }
  };

  // 游녢 Manejo de login con Google
  const handleGoogleSuccess = async (credentialResponse) => {
    const { credential } = credentialResponse;

    if (!credential) {
      setError('No se recibi칩 token de Google.');
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/api/usuarios/login-google', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tokenGoogle: credential }),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || 'Error al iniciar sesi칩n con Google.');
        return;
      }

      if (data.usuario && data.usuario.token) {
        const token = data.usuario.token;
        login(token);

        const decoded = jwtDecode(token);
        if (decoded.rol === 'superadmin' || decoded.rol_id === 1) {
          navigate('/admin/dashboard');
        } else if (decoded.rol === 'cliente' || decoded.rol_id === 2) {
          navigate('/cliente/dashboard');
        } else {
          setError('Rol no autorizado para ingresar.');
        }
      } else {
        setError('No se recibi칩 token del servidor.');
      }
    } catch (error) {
      console.error('[GoogleLogin] Error:', error);
      setError('Error al iniciar sesi칩n con Google.');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 4, width: '100%', maxWidth: 400 }}>
      <Typography variant="h5" align="center" gutterBottom>
        Iniciar Sesi칩n
      </Typography>
      <form onSubmit={handleSubmit}>
        <Box mb={2}>
          <TextField
            label="Correo electr칩nico"
            fullWidth
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
            required
            type="email"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <EmailIcon color="action" />
                </InputAdornment>
              ),
            }}
          />
        </Box>
        <Box mb={2}>
          <TextField
            label="Contrase침a"
            fullWidth
            value={contrase침a}
            onChange={(e) => setContrase침a(e.target.value)}
            required
            type={mostrarContrase침a ? 'text' : 'password'}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <LockIcon color="action" />
                </InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={toggleMostrarContrase침a} edge="end">
                    {mostrarContrase침a ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </Box>
        {error && (
          <Typography color="error" mb={2} textAlign="center">
            {error}
          </Typography>
        )}
        <Button type="submit" variant="contained" fullWidth size="large">
          Iniciar Sesi칩n
        </Button>
      </form>

      {/* L칤nea divisora */}
      <Divider sx={{ my: 2 }}>o</Divider>

      {/* Bot칩n de Google */}
      <Box display="flex" justifyContent="center">
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={() => setError('Fallo el inicio de sesi칩n con Google.')}
        />
      </Box>
    </Paper>
  );
};

export default LoginForm;
