// src/pages/cliente/LockersPage.jsx

import React, { useEffect, useState } from 'react';
import {
  Box, Grid, Paper, Typography, Button, Chip, Avatar,
  MenuItem, Select, FormControl, InputLabel, Stack, TextField
} from '@mui/material';
import {
  Lock as LockerIcon, Person as PersonIcon,
  PowerSettingsNew as EstadoIcon, Edit as EditIcon, Delete as DeleteIcon
} from '@mui/icons-material';
import Sidebar from '../../components/Layout/Sidebar';
import Topbar from '../../components/Layout/Topbar';
import { jwtDecode } from 'jwt-decode';

const API_URL = "http://localhost:5000";

const LockersPage = () => {
  const [lockers, setLockers] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [token, setToken] = useState('');
  const [empresaId, setEmpresaId] = useState(null);
  const [editValues, setEditValues] = useState({});
  const [newLocker, setNewLocker] = useState({
    identificador: '',
    ubicacion: '',
    tipo: '',
    estado: ''
  });

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (!storedToken) return;
    const decoded = jwtDecode(storedToken);
    setEmpresaId(decoded.empresa_id);
    setToken(storedToken);
  }, []);

  const fetchLockers = async () => {
    try {
      const res = await fetch(`${API_URL}/api/lockers`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setLockers(data);
    } catch (error) {
      console.error('Error al obtener lockers:', error);
    }
  };

  const fetchEmpleados = async () => {
    try {
      const res = await fetch(`${API_URL}/api/usuarios?rol_id=3`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setEmpleados(data);
    } catch (error) {
      console.error('Error al obtener empleados:', error);
    }
  };

  useEffect(() => {
    if (empresaId && token) {
      fetchLockers();
      fetchEmpleados();
    }
    // eslint-disable-next-line
  }, [empresaId, token]);

  const handleUpdateLocker = async (lockerId, values) => {
    try {
      const res = await fetch(`${API_URL}/api/lockers/${lockerId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(values),
      });
      const data = await res.json();
      if (!res.ok) {
        throw new Error(data?.error || 'Error al actualizar locker');
      }
      fetchLockers();
    } catch (error) {
      console.error('Error al actualizar locker:', error.message);
    }
  };

  const handleDeleteLocker = async (lockerId) => {
    if (!window.confirm('Â¿Seguro que deseas eliminar este locker?')) return;
    try {
      const res = await fetch(`${API_URL}/api/lockers/${lockerId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await res.json();
      if (!res.ok) {
        throw new Error(data?.error || 'Error al eliminar locker');
      }
      fetchLockers();
    } catch (error) {
      console.error('Error al eliminar locker:', error.message);
    }
  };

  const handleChange = (lockerId, field, value) => {
    setEditValues((prev) => ({
      ...prev,
      [lockerId]: {
        ...prev[lockerId],
        [field]: value,
      },
    }));
  };

  const handleNewLockerChange = (field, value) => {
    setNewLocker((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleCreateLocker = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/api/lockers`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newLocker),
      });
      const data = await res.json();
      if (!res.ok) {
        throw new Error(data?.error || 'Error al crear locker');
      }
      setNewLocker({
        identificador: '',
        ubicacion: '',
        tipo: 'frios',
        estado: 'activo'
      });
      fetchLockers();
    } catch (error) {
      console.error('Error al crear locker:', error.message);
    }
  };

  return (
    <Box display="flex">
      <Sidebar />
      <Box flexGrow={1} p={3}>
        <Topbar title="GestiÃ³n de Lockers" />
        <Paper sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6">Lockers por Empresa</Typography>
          <Typography variant="body2" color="text.secondary">
            Edita, asigna, administra, elimina y crea nuevos lockers.
          </Typography>
        </Paper>

        {/* Formulario para crear un nuevo locker */}
        <Paper sx={{ p: 2, mb: 4 }}>
          <Typography variant="subtitle1" sx={{ mb: 2 }}>Agregar nuevo locker</Typography>
          <form onSubmit={handleCreateLocker}>
            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
              <TextField
                label="Identificador"
                value={newLocker.identificador}
                onChange={(e) => handleNewLockerChange('identificador', e.target.value)}
                required
              />
              <TextField
                label="UbicaciÃ³n"
                value={newLocker.ubicacion}
                onChange={(e) => handleNewLockerChange('ubicacion', e.target.value)}
                required
              />
              <FormControl>
                <InputLabel>Tipo</InputLabel>
                <Select
                  value={newLocker.tipo}
                  label="Tipo"
                  onChange={(e) => handleNewLockerChange('tipo', e.target.value)}
                >
                  <MenuItem value="frios">FrÃ­os</MenuItem>
                  <MenuItem value="perecederos">Perecederos</MenuItem>
                  <MenuItem value="no_perecederos">No Perecederos</MenuItem>
                </Select>
              </FormControl>
              <FormControl>
                <InputLabel>Estado</InputLabel>
                <Select
                  value={newLocker.estado}
                  label="Estado"
                  onChange={(e) => handleNewLockerChange('estado', e.target.value)}
                >
                  <MenuItem value="activo">Activo</MenuItem>
                  <MenuItem value="inactivo">Inactivo</MenuItem>
                </Select>
              </FormControl>
              <Button type="submit" variant="contained" color="primary">
                Agregar
              </Button>
            </Stack>
          </form>
        </Paper>

        <Grid container spacing={3}>
          {lockers.map((locker) => {
            const isActivo = locker.estado === 'activo';
            const edit = editValues[locker.id] || {};

            return (
              <Grid item xs={12} sm={6} md={4} lg={3} key={locker.id}>
                <Paper
                  sx={{
                    p: 2,
                    borderRadius: 3,
                    backgroundColor: isActivo ? '#f1f8e9' : '#ffebee',
                    border: `2px solid ${isActivo ? '#43a047' : '#e53935'}`,
                    boxShadow: 4,
                  }}
                >
                  <Stack spacing={1}>
                    <Stack direction="row" alignItems="center" spacing={1}>
                      <LockerIcon color={isActivo ? 'success' : 'error'} />
                      <TextField
                        size="small"
                        label="Identificador"
                        fullWidth
                        value={edit.identificador ?? locker.identificador}
                        onChange={(e) => handleChange(locker.id, 'identificador', e.target.value)}
                      />
                    </Stack>
                    <TextField
                      size="small"
                      label="UbicaciÃ³n"
                      fullWidth
                      value={edit.ubicacion ?? locker.ubicacion}
                      onChange={(e) => handleChange(locker.id, 'ubicacion', e.target.value)}
                    />
                    <FormControl size="small" fullWidth>
                      <InputLabel>Tipo</InputLabel>
                      <Select
                        value={edit.tipo ?? locker.tipo}
                        label="Tipo"
                        onChange={(e) => handleChange(locker.id, 'tipo', e.target.value)}
                      >
                        <MenuItem value="frios">FrÃ­os</MenuItem>
                        <MenuItem value="perecederos">Perecederos</MenuItem>
                        <MenuItem value="no_perecederos">No Perecederos</MenuItem>
                      </Select>
                    </FormControl>

                    <FormControl size="small" fullWidth>
                      <InputLabel>Empleado</InputLabel>
                      <Select
                        value={edit.usuario_id !== undefined ? edit.usuario_id : locker.usuario_id ?? ''}
                        label="Empleado"
                        onChange={(e) =>
                          handleChange(
                            locker.id,
                            'usuario_id',
                            e.target.value === '' ? null : e.target.value
                          )
                        }
                      >
                        <MenuItem value="">
                          <em>Sin asignar</em>
                        </MenuItem>
                        {empleados
                          .filter((emp) => emp.rol_id === 3 && emp.empresa_id === empresaId)
                          .map((emp) => (
                            <MenuItem key={emp.id} value={emp.id}>
                              {emp.nombre}
                            </MenuItem>
                          ))}
                      </Select>
                    </FormControl>

                    {locker.usuario?.nombre && (
                      <Chip
                        avatar={
                          <Avatar>
                            <PersonIcon />
                          </Avatar>
                        }
                        label={`Asignado: ${locker.usuario.nombre}`}
                        color="secondary"
                        sx={{ mt: 1 }}
                      />
                    )}

                    <Stack direction="row" spacing={1}>
                      <Button
                        startIcon={<EstadoIcon />}
                        variant="contained"
                        color={isActivo ? 'error' : 'success'}
                        onClick={() =>
                          handleUpdateLocker(locker.id, {
                            estado: isActivo ? 'inactivo' : 'activo',
                            usuario_id: isActivo ? null : locker.usuario_id,
                          })
                        }
                      >
                        {isActivo ? 'Desactivar' : 'Activar'}
                      </Button>

                      <Button
                        startIcon={<EditIcon />}
                        variant="outlined"
                        onClick={() => {
                          const updatedUsuarioId =
                            edit.usuario_id !== undefined
                              ? edit.usuario_id
                              : locker.usuario_id ?? null;
                          const valuesToUpdate = {
                            identificador: edit.identificador ?? locker.identificador,
                            ubicacion: edit.ubicacion ?? locker.ubicacion,
                            tipo: edit.tipo ?? locker.tipo,
                            usuario_id: updatedUsuarioId,
                          };

                          // Si no hay usuario asignado, tambiÃ©n desactiva el locker
                          if (updatedUsuarioId === null) {
                            valuesToUpdate.estado = 'inactivo';
                          }

                          handleUpdateLocker(locker.id, valuesToUpdate);
                        }}
                      >
                        Guardar cambios
                      </Button>

                      <Button
                        startIcon={<DeleteIcon />}
                        variant="outlined"
                        color="error"
                        onClick={() => handleDeleteLocker(locker.id)}
                      >
                        Eliminar
                      </Button>
                    </Stack>
                  </Stack>
                </Paper>
              </Grid>
            );
          })}
        </Grid>
      </Box>
    </Box>
  );
};

export default LockersPage;
